def count():
	total_attacks = []
	try:
		with open("./AllAttacks.txt", 'r') as file:
			total_attacks.append(int(file.read()))
	except:
		with open("./AllAttacks.txt", 'w') as file:
			file.write("0")
	try:
		with open("./AllAttacks.txt", 'w') as file:
			file.write(str(total_attacks[0] + 1))
	except:
		with open("./AllAttacks.txt", 'w') as file:
			file.write("0")

	with open("./AllAttacks.txt", 'r') as file:
		total_attacks.clear()
		total_attacks.append(file.read().strip())

	for total in total_attacks:
		return total
